build() {
    Stack({ alignContent: Alignment.Bottom }) {
      Column() {
        Text(this.input.length == 0 ? '0' : this.input) // 输入内容，若没有内容显示0
          .width('100%')
          .padding(10)
          .textAlign(TextAlign.End)
          .fontSize(46)
        Grid() {
          ForEach(this.BTN_INFO_ARRAY, btnInfo => {
            this.Btn(btnInfo)
          }) // 遍历生成按钮
        }
        .columnsTemplate('1fr 1fr 1fr 1fr') // 按钮比重分配
        .rowsTemplate('1fr 1fr 1fr 1fr 1fr')
        .columnsGap(2) // 按钮间隙
        .rowsGap(2)
        .width('100%')
        .aspectRatio(1) // 长宽比
      }
    }.width('100%').height('100%').backgroundColor(0xeeeeee)
  }

