build() {
    NavDestination() {
      Scroll() {
        Column() {
          // Back button.
          Row() {
            Button({ type: ButtonType.Normal }) {
              Image($r('app.media.hwid_next_ic_appback'))
                .backgroundColor(Color.Transparent)
                .borderRadius(20)
                .width(40)
                .height(40)
                .draggable(false)
                .autoResize(false)
                .focusable(true)
                .fillColor($r('sys.color.ohos_id_color_titlebar_icon'))
                .matchTextDirection(true)
            }
            .alignSelf(ItemAlign.Start)
            .backgroundColor($r('sys.color.ohos_id_color_button_normal'))
            .borderRadius(20)
            .width(40)
            .height(40)
            .id('quickLoginAppBackButton')
            .onClick(() => {
              AvoidRepeatClick.avoidRepeatClick(() => {
                if (this.pageInfos && this.pageInfos.size() > 0) {
                  this.pageInfos.pop();
                }
              })
            })
          }
          .height(56)
          .padding({ top: 8, bottom: 8 })
          .width('100%')
          .margin({
            top: (this.isPhone && this.isLandScape || this.isDevice2in1) ? 0 : 36,
            left: this.isPhone && this.isLandScape ? -16 : 0
          })
          .zIndex(2024);

          Flex({
            direction: this.isPhone && this.isLandScape ? FlexDirection.Row : FlexDirection.Column,
            alignItems: ItemAlign.Center
          }) {
            Column() {
              // App icon.
              Row() {
                Image($r('app.media.appmarket'))
                  .width(this.isPhone ? 80 : 96)
                  .height(this.isPhone ? 80 : 96)
                  .draggable(false)
                  .copyOption(CopyOptions.None)
                  .borderRadius(20)
                  .onComplete(() => {
                    hilog.info(domainId, logTag, 'Succeeded in loading appIcon.');
                  })
                  .onError(() => {
                    hilog.error(domainId, logTag, 'Failed to load appIcon.');
                  })
              }
              .height(this.isPhone && this.isLandScape ? 80 : '100%')

              // Mobile number and its description.
              Column() {
                Text(this.quickLoginAnonymousPhone)
                  .fontSize(36)
                  .fontColor($r('sys.color.ohos_id_color_text_primary'))
                  .fontFamily($r('sys.string.ohos_id_text_font_family_medium'))
                  .fontWeight(FontWeight.Bold)
                  .lineHeight(48)
                  .textAlign(TextAlign.Center)
                  .maxLines(1)
                  .constraintSize({ maxWidth: '100%', minHeight: 48 })

                Text($r('app.string.bind_huaweiId_number'))
                  .fontSize(12)
                  .fontColor($r('sys.color.ohos_id_color_text_secondary'))
                  .fontFamily($r('sys.string.ohos_id_text_font_family_regular'))
                  .fontWeight(FontWeight.Regular)
                  .lineHeight(16)
                  .textAlign(TextAlign.Center)
                  .maxLines(1)
                  .constraintSize({ maxWidth: '100%' })
                  .margin({
                    top: 8
                  })
              }
              .margin({
                top: this.isPhone && this.isLandScape ? 32 : -36
              })
            }
            .width(this.isPhone && this.isLandScape ? '50%' : '100%')
            .height(this.isPhone && this.isLandScape ? '100%' : this.isBigDisplay ? '50%' : '40%')
            .justifyContent(this.isPhone && this.isLandScape ? FlexAlign.Center : FlexAlign.SpaceBetween);

            Column() {
              // Two sign-in buttons.
              Column() {
                // One-tap sign-in.
                Column() {
                  LoginWithHuaweiIDButton({
                    params: {
                      // Custom button style.
                      style: loginComponentManager.Style.BUTTON_CUSTOM,
                      extraStyle: {
                        buttonStyle: new loginComponentManager.ButtonStyle().loadingStyle({
                          show: true
                        })
                      },
                      loginType: loginComponentManager.LoginType.QUICK_LOGIN,
                      supportDarkMode: true
                    },
                    controller: this.controller
                  }).id('login_with_huaweiId_button')
                }
                .height(40)
                .width('100%')
                .constraintSize({
                  minHeight: 40,
                  maxWidth: 448
                })

                // Other ways to sign in.
                Column() {
                  Button({
                    type: ButtonType.Capsule,
                    stateEffect: true
                  }) {
                    Text($r('app.string.other_login_models'))
                      .fontColor($r('sys.color.ohos_id_color_text_primary'))
                      .fontFamily($r('sys.string.ohos_id_text_font_family_medium'))
                      .fontWeight(FontWeight.Medium)
                      .fontSize($r('sys.float.ohos_id_text_size_button1'))
                      .focusable(true)
                      .focusOnTouch(true)
                      .id('loginPanel_huawei_id_other_login_btn_text')
                      .textOverflow({ overflow: TextOverflow.Ellipsis })
                      .maxLines(1)
                      .padding({ left: 8, right: 8 })
                  }
                  .id('loginPanel_other_login_btn')
                  .fontColor($r('sys.color.font_primary'))
                  .fontFamily($r('sys.string.ohos_id_text_font_family_medium'))
                  .fontWeight(FontWeight.Medium)
                  .backgroundColor($r('sys.color.ohos_id_color_button_normal'))
                  .focusable(true)
                  .opacity(this.enableStatus ? 1 : 0.6)
                  .focusOnTouch(true)
                  .enabled(this.enableStatus)
                  .width('100%')
                  .constraintSize({
                    minHeight: 40,
                    maxWidth: 448
                  })
                  .onClick(() => {
                    // Redirect to the screen for another way to sign in.
                    AvoidRepeatClick.avoidRepeatClick(() => {
                      // Redirect to the custom sign-in screen.
                      if (this.enableStatus) {
                        const params: Record<string, Object> = { 'isFromQuickLogin': true };
                        this.pageInfos?.pushPathByName('OtherLoginPage', params, (popInfo: PopInfo) => {
                          if (popInfo.result) {
                            // Receive data after a successful sign-in.
                            hilog.info(domainId, logTag,
                              `pop to PrepareLoginPage result: ${JSON.stringify(popInfo.result)}}`);
                            this.pageInfos?.popToName('PrepareLoginPage', popInfo.result, true);
                          }
                        }, true);
                      }
                    });
                  })
                }.margin({ top: 16 })
              }
              .margin({
                top: this.isPhone && this.isLandScape ? 0 : 92
              })

              // Agreement.
              Row() {
                UserAgreement({
                  isSelected: this.isSelected,
                  privacyText: this.privacyText,
                  enableStatus: this.enableStatus,
                  onChange: (value) => {
                    this.checkboxChange(value);
                  }
                })
              }
              .margin({
                top: 24,
                bottom: this.isPhone && this.isLandScape ? 0 : (this.isDevice2in1 ? 16 : this.aiBarHeight + 16)
              })
            }
            .height(this.isPhone && this.isLandScape ? '100%' : (this.isBigDisplay ? '50%' : '60%'))
            .width(this.isPhone && this.isLandScape ? '50%' : '100%')
            .margin({
              left: this.isPhone && this.isLandScape ? 40 : 0
            })
            .justifyContent(this.isPhone && this.isLandScape ? FlexAlign.Center : FlexAlign.SpaceBetween)
          }
          .width('100%')
          .height('100%')
          .margin({ top: (this.isPhone && this.isLandScape || this.isDevice2in1) ? -56 : -92 })
        }
        .padding({
          left: this.isPhone && this.isLandScape ? 40 : 16,
          right: this.isPhone && this.isLandScape ? 40 : 16
        })
        .justifyContent(FlexAlign.SpaceBetween)
      }
      .backgroundColor($r('sys.color.ohos_id_color_sub_background'))
      .width('100%')
      .height('100%')
    }

