build() {
    Column() {
      /*天气信息*/
      Column(){
        Row() {
          Text('西安')
            .fontSize(18)
          Image($r('app.media.img'))
            .width(15)
            .height(15)
            .margin({ left: 5 })
        }

        Row() {
          Text(this.realtime.temperature)
            .fontSize(60)

          Text('℃')
            .fontSize(15)
            .margin({ top: 10 })
        }
        .alignItems(VerticalAlign.Top)
        .margin({ top: 5 })

        Row() {
          Text(this.realtime.info)
            .fontSize(16)
          Divider()
            .vertical(true)
            .height(16)
            .margin({ left: 7, right: 7 })
          Text(this.realtime.direct)
            .fontSize(17)
        }
        .alignItems(VerticalAlign.Center)
        .margin({ top: 5 })

      }
      .width('100%')
      .margin({top:50})

      /*天气列表*/

      // 往后天气
      Column() {
        Row() {
          this.WeatherText('日期')
          this.WeatherText('温度')
          this.WeatherText('天气')
          this.WeatherText('风向')
        }.backgroundColor('#fffc6746')

        ForEach(this.future, (item: WeatherData) => {
          Row() {
            this.WeatherText(item.date)
            this.WeatherText(item.temperature)
            this.WeatherText(item.weather)
            this.WeatherText(item.direct)
          }
        },( item : string) => item)
      }
      .padding(10)
      .margin({ top: 20 })

      Text(this.loadingHint)
        .textAlign(TextAlign.Center)
        .fontSize(20)
        .width('100%')
        .margin({top:30,bottom:30})
        .visibility(this.isRequestSucceed == true ? Visibility.Hidden : Visibility.Visible)

      Row(){
        Button('获取天气数据').width('95%').fontSize(20).padding(10)
          .onClick(()=>{
            this.loadingHint = '加载中...'
            this.getWeather()
          })
      }
      .width('100%')
      .justifyContent(FlexAlign.Center)
      .margin({bottom:20,top:20})

      Blank()
    }
    .width('100%')
    .height('100%')
    .justifyContent(FlexAlign.End)
  }
