build() {
    Row() {
      Column() {
        TextInput({ placeholder: getContext().resourceManager.getStringByNameSync('Text_String1')  })
          .onChange((v) => {
            this.encode(v)
          })
        Row() {
          Text('length:')
          Text(this.length + '')
        }.width('90%')

        Row() {
          Text('toByteArray:')
          Text(Unit8ArrayUtils.Uint8ArrayToString(this.toByteArray))
        }.width('90%')

        Row() {
          Text('fromByteArray:')
          Text(this.fromByteArray)
        }.width('90%')
      }.justifyContent(FlexAlign.Start)
      .width('100%')
      .height('85%')
    }
    .height('100%')
  }
