                Text(this.videoInfo?.stat?.danmaku ? `${utils.formatNumber(this.videoInfo?.stat?.danmaku)}` : '0')
                  .fontSize(12)
                  .fontColor($r('app.color.colorGray')).margin({ right: 10 })

