              .width('100%')
              .margin({ top: 10, bottom: 16 })
              .justifyContent(FlexAlign.Start)
              .alignItems(VerticalAlign.Center)

              Row() {
                Button() {
                  Column() {
                    Image(this.likeStat == 1 ? $r('app.media.icon_like_active') : $r('app.media.icon_like_default'))
                      .width('24vp')
                      .fillColor(Color.Gray)
                    Text(this.videoInfo?.stat?.like ? `${utils.formatNumber(this.videoInfo?.stat?.like)}` : '0')
                      .fontColor(this.likeStat == 1 ? $r('app.color.colorPrimary') : $r('app.color.colorText'))
                      .fontSize('12vp').fontColor(Color.Gray)
                  }
