                .width('30vp')
                .backgroundColor(Color.Transparent)
                .layoutWeight(1)
                .onClick(() => this.commit('like'))

                Button() {
                  Column() {
                    Image($r('app.media.icon_dislike_default'))
                      .width('24vp')
                      .fillColor(Color.Gray)
                    Text(this.videoInfo?.stat?.dislike ? `${utils.formatNumber(this.videoInfo?.stat?.dislike)}` : '0')
                      .fontSize('12vp').fontColor(Color.Gray)
                  }
                }
                .width('30vp')
                .backgroundColor(Color.Transparent)
                .layoutWeight(1)
                .onClick(() => this.commit('dislike'))

