                  Column() {
                    Image($r('app.media.icon_coin_default'))
                      .width('24vp')
                      .fillColor(Color.Gray)
                    Text(this.videoInfo?.stat?.coin ? `${utils.formatNumber(this.videoInfo?.stat?.coin)}` : '0')
