                    Image(this.favorite ? $r('app.media.icon_collect_active') : $r('app.media.icon_collect_default'))
                      .width('24vp')
                      .fillColor(Color.Gray)
                    Text(this.videoInfo?.stat?.favorite ? `${utils.formatNumber(this.videoInfo?.stat?.favorite)}` : '0')
