            }
            .padding(8)
            .height('100%')
          }
          .width('100%')
          .height('100%')
          .scrollBarWidth(1)
          .scrollBar(BarState.Auto)
          .scrollBarColor(Color.White)
          .edgeEffect(EdgeEffect.Spring)
