build() {
    NavDestination() {
      Stack() {
        WaterFlow() {
          LazyForEach(this.dataSource, (item: CardAttr, index: number) => {
            FlowItem() {
              CardComponent({
                indexValue: index.toString(),
                cardAttr: item,
                onColumnClicked: (indexValue: string, event: ClickEvent) => {
                  this.onColumnClicked(indexValue, event, Constants.getFlowItemIdByIndex(index.toString()));
                }
              })
                .width(this.columnWidth)
            }
            .borderRadius(RADIUS)
            .clip(true)
            .id(Constants.getFlowItemIdByIndex(index.toString()))
          }, (item: string) => item)
        }
        .columnsTemplate(this.columnType)
        .columnsGap(5)
        .rowsGap(5)
        .width('100%')
        .height('100%')
      }
      .size({ width: '100%', height: '100%' })
      .padding({ left: 10, right: 10 })
    }
    .title('卡片一镜到底首页')
    .onReady((context: NavDestinationContext) => {
      this.pageInfos = context.pathStack;
    })
  }

