build() {
    if (!this.isLargeSize) {
      Column() {
        this.MyTitleBuilder()

        Column() {
          Image($r('app.media.img_' + (this.indexValue % 6)))
            .size({ width: '100%' })
            .objectFit(ImageFit.Auto)
            .visibility(this.isVisible)
            .onClick((event?: ClickEvent) => {
              if (!event) {
                return;
              }
              let params: Record<string, Object> = {};
              params['imageSource'] = $r('app.media.img_' + (this.indexValue % 6));
              let transitionParam = new LongTakeTransitionParam(ezCustomTransition.getRectInfoByClickEvent(event), {
                onEnterTransitionStart: () => { this.isVisible = Visibility.Hidden },
                onBackTransitionEnd: () => { this.isVisible = Visibility.Visible }
              });

              ezCustomTransition.imageLongTakeTransition('test', transitionParam, (transitionParam: LongTakeTransitionParam) => {
                params['transitionParam'] = transitionParam;
                this.pageInfos.pushPath({ name: 'PageThree', param: params });
              })
            })
        }
        .width('80%')
        .id('test')
        .position({ y: 100 })

        Text($r('app.string.DetailPage_text'))
          .width(px2vp(WindowUtils.windowWidth_px))
          .position({ y: 350 })
      }
      .size({ width: '100%', height: '100%' })
      .backgroundColor(Constants.DEFAULT_BG_COLOR)
      .expandSafeArea([SafeAreaType.SYSTEM], [SafeAreaEdge.TOP])
    } else {
      Row() {
        Stack() {
          Image($r('app.media.img_' + (this.indexValue % 6)))
            .size({ width: '80%' })
            .objectFit(ImageFit.Auto)
            .id('test')
            .visibility(this.isVisible)
            .onClick((event?: ClickEvent) => {
              if (!event) {
                return;
              }
              let params: Record<string, Object> = {};
              params['imageSource'] = $r('app.media.img_' + (this.indexValue % 6));
              let transitionParam = new LongTakeTransitionParam(ezCustomTransition.getRectInfoByClickEvent(event), {
                onEnterTransitionStart: () => { this.isVisible = Visibility.Hidden },
                onBackTransitionEnd: () => { this.isVisible = Visibility.Visible }
              });

              ezCustomTransition.imageLongTakeTransition('test', transitionParam, (transitionParam: LongTakeTransitionParam) => {
                params['transitionParam'] = transitionParam;
                this.pageInfos.pushPath({ name: 'PageThree', param: params });
              })
            })
        }
        .width('50%')
        .height('100%')

        Column() {
          this.MyTitleBuilder()

          Text($r('app.string.DetailPage_text'))
            .width('100%')
            .height('100%')
            .align(Alignment.TopStart)
        }
        .width('50%')
        .height('100%')
      }
      .size({ width: '100%', height: '100%' })
