@Builder
  MyTitleBuilder() {
    Row({ space: 10 }) {
      Image($r('app.media.ic_public_back'))
        .width(24)
        .height(24)
        .onClick(() => {
          this.onBackPressed();
        })
        .margin({ left: 20 })

      Text('详情页')
        .height(40)
        .fontSize(25)
        .fontColor(Color.Black)
        .width('100%')
    }
    .clip(true)
    .width('100%')
    .height(px2vp(TITLE_HEIGHT_PX))
    .alignItems(VerticalAlign.Center)
  }

