build() {
    NavDestination() {
      LongTakeTransitionDelegate({
        contentBuilder: () => { this.MyContent() },
        longTakeSession: this.longTakeSession
      })
    }
    .backgroundColor(this.longTakeSession.navDestinationBgColor)
    .hideTitleBar(true)
    .onReady((context: NavDestinationContext) => {
      let param = context.pathInfo?.param as Record<string, Object>;
      let longTakeTransitionParam = param['transitionParam'] as LongTakeTransitionParam;
      this.pathStack = context.pathStack;
      this.imageSource = param['imageSource'] as ResourceStr;

      this.longTakeSession.init(context, longTakeTransitionParam, 'PageThree_Id');
    })
    .EnterNextPageGestureStyle()
    .ExitGestureStyle()
  }

@Builder
  MyContent() {
    Stack({ alignContent: Alignment.Top }) {
      Stack() {
        Image(this.imageSource)
          .size({ width:'100%' })
          .objectFit(ImageFit.Auto)
      }
      .size({ width: '100%', height: 700 })
      .id('PageThree_Id')
    }
    .size({ width: '100%', height: '100%' })
    .backgroundColor(Color.Black)
    .expandSafeArea([SafeAreaType.SYSTEM])
  }
