        .barWidth("100%")
        .barHeight("56vp")
        .barPosition(BarPosition.End)
        .barMode(BarMode.Fixed)
        .onChange((index) => {
          this.selectIndex = index
        })
      }
    }.navDestination(this.pageMap).hideTitleBar(true).mode(NavigationMode.Stack)
  }

