              }.width('100%').height('60vp').padding({ left: '5vp', right: '5vp' })

              List() {
                ForEach(item.data.itemList, (item: FocusModelItemListDataItemList, index: number) => {
                  ListItem() {
                    Stack() {
                      Image(item.data?.cover?.detail)
                        .width('100%')
                        .height('100%')
                        .border({ radius: '10vp' })
                      Text(item.data?.category)
                        .fontSize('12fp')
                        .fontColor('#fff')
                        .width('45vp')
                        .height('20vp')
                        .position({ x: 0, y: 0 })
